PROTEA_PATH = GetPluginInfo(GetPluginID(), 6):gsub('protea.xml', '') package.path = package.path .. ';' .. PROTEA_PATH .. '?.lua'
protea = require 'core.init'
-- === === === === === === === === === === === === === === === === === === ==== -- MUSHCLIENT PLUGIN CALLBACKS -- === === === === === === === === === === === === === === === === === === ====
host2realm = { ['achaea.com'] = 'achaea', ['aetolia.com'] = 'aetolia', ['imperian.com'] = 'imperian', ['lusternia.com'] = 'lusternia', }
buffer = ''
function OnPluginConnect() buffer = '' local realm = host2realm[GetInfo(1)] if realm then atcp:Initialize() protea:Environment('realm', realm) end end -- OnPluginConnect()
if not GetInfo(106) then local realm = host2realm[GetInfo(1)] if realm then protea:Environment('realm', realm) end end
function OnPluginDisconnect() protea:EnvironmentReset() end -- OnPluginDisconnect()
function OnPluginPacketReceived(packet) local packet = atcp:Parse(buffer .. packet)
-- As long as last two characters is not a GO AHEAD, we have incomplete data if GetInfo(103) and #packet > 0 and string.sub(packet, #packet - 1) ~= '\255\249' then buffer = packet return '' end
trigger:Parse('mud', packet) state:Flush()
buffer = '' return packet end -- OnPluginPacketReceived()
function OnPluginSend(input) if not trigger:Parse('user', input) then command:Queue(input) end return false end -- OnPluginSend()
